import React, { useState } from "react";
import axios from "axios";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";

const SearchComponent = () => {
  const [query, setQuery] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent the default form submission behavior

    if (!query) return; // Exit if the query is empty

    try {
      const response = await axios.get(`YOUR_API_ENDPOINT?search=${query}`);
      console.log(response.data); // Log the response data or set it to state
      // Process your response data here
    } catch (error) {
      console.error("Error fetching data: ", error);
      // Handle error here
    }
  };

  return (
    <div >
    <Box

      component="form"
      onSubmit={handleSearch}
      sx={{
        display: "flex",
        alignItems: "center",
        gap: "10px",
      }}
    >
      <TextField

        id="outlined-basic"
        label="Search"
        variant="outlined"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        sx={{
          input: { color: "white" },
          "& .MuiOutlinedInput-root": {
            "& fieldset": {
              borderColor: "white",
            },
            "&:hover fieldset": {
              borderColor: "white",
            },
            "&.Mui-focused fieldset": {
              borderColor: "white",
            },
          },
          "& .MuiInputLabel-outlined": {
            color: "white",
          },
        }}
      />
      <Button
        type="submit"
        variant="contained"
        sx={{
          bgcolor: "grey", // Set the button color to grey
          color: "white", // Set the text color to white
          "&:hover": {
            bgcolor: "darkgrey", // Darken the button color slightly on hover
          },
        }}
      >
        Search
      </Button>
      </Box>
      </div>
  );
};

export default SearchComponent;
