import React, { useState } from 'react';
import axios from 'axios';
import TextField from '@mui/material/TextField';
import IconButton from '@mui/material/IconButton'; // Import IconButton for icon-only button
import Box from '@mui/material/Box';
import ExitToAppIcon from '@mui/icons-material/ExitToApp'; // Import the ExitToApp icon

const SearchComponent = () => {
  const [query, setQuery] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent the default form submission behavior

    if (!query) return; // Exit if the query is empty

    try {
      const response = await axios.get(`YOUR_API_ENDPOINT?search=${query}`);
      console.log(response.data); // Log the response data or set it to state
      // Process your response data here
    } catch (error) {
      console.error('Error fetching data: ', error);
      // Handle error here
    }
  };

  return (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      minHeight="100vh"
    >
      <Box
        component="form"
        onSubmit={handleSearch}
        sx={{
          display: 'flex',
          alignItems: 'center',
          gap: '10px',
        }}
      >
        <TextField
          id="outlined-basic"
          label="Search"
          variant="outlined"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          sx={{
            input: { color: 'white' },
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: 'white',
              },
              '&:hover fieldset': {
                borderColor: 'white',
              },
              '&.Mui-focused fieldset': {
                borderColor: 'white',
              },
            },
            '& .MuiInputLabel-outlined': {
              color: 'white',
            },
          }}
        />
        <IconButton
          type="submit"
          color="default" // Set the button color to the default theme color or customize as needed
          sx={{
            '&:hover': {
              bgcolor: 'rgba(255, 255, 255, 0.2)', // Lighten background color on hover
            },
          }}
        >
          <ExitToAppIcon sx={{ color: 'grey' }} /> {/* Set the icon color to grey */}
        </IconButton>
      </Box>
    </Box>
  );
};

export default SearchComponent;
