import React, { useState } from "react";
import axios from "axios";
import TextField from "@mui/material/TextField";
import IconButton from "@mui/material/IconButton";
import Box from "@mui/material/Box";
import ExitToAppIcon from "@mui/icons-material/ExitToApp";

const SearchComponent = () => {
  const [query, setQuery] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent the default form submission behavior

    if (!query) return; // Exit if the query is empty

    try {
      const response = await axios.get(`YOUR_API_ENDPOINT?search=${query}`);
      console.log(response.data); // Log the response data or set it to state
      // Process your response data here
    } catch (error) {
      console.error("Error fetching data: ", error);
      // Handle error here
    }
  };

  return (
    <Box
      sx={{
        position: "fixed",
        bottom: 0,
        left: 0,
        right: 0,
        display: "flex",
        justifyContent: "center",
        p: 2,
        bgcolor: "transparent", // Ensure the background is completely transparent
      }}
    >
      <Box
        component="form"
        onSubmit={handleSearch}
        sx={{
          display: "flex",
          alignItems: "center",
          gap: "10px",
        }}
      >
        <TextField
          id="outlined-basic"
          label="Search"
          variant="outlined"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          sx={{
            ".MuiInputBase-input": {
              color: "white", // White text color
            },
            ".MuiOutlinedInput-root": {
              backgroundColor: "transparent", // Transparent background for the text box
              "& fieldset": {
                borderColor: "white", // White outline
              },
              "&:hover fieldset": {
                borderColor: "white", // Maintain white outline on hover
              },
              "&.Mui-focused fieldset": {
                borderColor: "white", // Maintain white outline when focused
              },
            },
            ".MuiInputLabel-outlined": {
              color: "white", // White label color
            },
          }}
        />
        <IconButton
          type="submit"
          sx={{
            color: "purplse", // Icon color set to green
            "&:hover": {
              bgcolor: "rgba(255, 255, 255, 0.1)", // Slightly lighter background on hover for better user interaction
            },
          }}
        >
          <ExitToAppIcon />
        </IconButton>
      </Box>
    </Box>
  );
};

export default SearchComponent;
