import React, { useState } from "react";
import axios from "axios";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";

const SearchComponent = () => {
  const [query, setQuery] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent the default form submission behavior

    if (!query) return; // Exit if the query is empty

    try {
      const response = await axios.get(`YOUR_API_ENDPOINT?search=${query}`);
      console.log(response.data); // Log the response data or set it to state
      // Process your response data here
    } catch (error) {
      console.error("Error fetching data: ", error);
      // Handle error here
    }
  };

  return (
    <form onSubmit={handleSearch} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '20px' }}>
      <TextField
        id="outlined-basic"
        label="Search"
        variant="outlined"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        sx={{
          input: { color: 'white' }, // Text color
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: 'white', // Border color
            },
            '&:hover fieldset': {
              borderColor: 'white', // Border color on hover
            },
            '&.Mui-focused fieldset': {
              borderColor: 'white', // Border color on focus
            }
          },
          '& .MuiInputLabel-outlined': {
            color: 'white', // Label color
          },
        }}
      />
      <Button
        type="submit"
        variant="contained"
        color="primary"
        sx={{
          color: 'white', // Text color
          borderColor: 'white', // Border color
          '&:hover': {
            borderColor: 'white', // Border color on hover
            backgroundColor: 'rgba(255, 255, 255, 0.2)', // Lighten background color on hover
          },
        }}
      >
        Search
      </Button>
    </form>
  );
};

export default SearchComponent;
